draw((-1/3,0)--(2 + 0.2,0));
draw((-1.1,3)--(-0.9,3));
draw((0,-0.1)--(0,0.1));
draw((1,-0.1)--(1,0.1));
draw((2,-0.1)--(2,0.1));
draw((0,3)--(1,0)--(2,3));

